/*Table structure for table `t_article` */

CREATE TABLE `t_article` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(128) DEFAULT NULL COMMENT '文章标题',
  `content` text COMMENT '文章内容',
  `state` int(11) DEFAULT '0' COMMENT '状态',
  `type` int(11) DEFAULT NULL COMMENT '文章类型',
  `image` varchar(255) DEFAULT NULL COMMENT '图片',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `update_time` datetime DEFAULT NULL COMMENT '更新时间',
  `sort` int(11) DEFAULT NULL COMMENT '排序',
  `remark` varchar(128) DEFAULT NULL COMMENT '概述',
  `source` varchar(128) DEFAULT NULL COMMENT '来源',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=22 DEFAULT CHARSET=utf8;

/*Data for the table `t_article` */

insert  into `t_article`(`id`,`title`,`content`,`state`,`type`,`image`,`create_time`,`update_time`,`sort`,`remark`,`source`) values (1,'抗日神剧再现奇葩桥段：裤裆藏雷','<p>\r\n	正在播映的抗日剧《一起打鬼子》就在你身后紧追不放看到没？百度的剧情梗概说的是：上世纪40年代初，河南遭受大旱，人称“马三爷”的河南女地主马\r\n秀芹（许晴饰演）利用半屏山前清时期开辟的暗河而拥有丰富的水源在精心播种下喜获丰收，自然引起了国、共、日三方面的注意，一场为粮食的斗争就此激烈展\r\n开。最终，一场蝗灾袭来，不仅拯救了大量灾民，也拯救了马秀芹，坚定了她跟共产党走的信心。当日军再次进攻，妄图抢夺粮食时，国共联手抗日保粮，取得了最\r\n后的胜利。\r\n</p>\r\n<p>\r\n	感觉没啥问题啊？直到网友发现并上传了这样的桥段：\r\n</p>\r\n<p>\r\n	翔嫂葛天扮演的“银妹”去看望被皇军关押折磨的爱人，然后情难自已，让爱人先摸自己的胸，然后再……\r\n</p>\r\n<p>\r\n	原来如此啊……是不是佩服的五体投地了？小编已经被这桥段紧紧抓住，刷剧去了~\r\n</p>',0,1,'/upload/2015/5/18/1431921560620.png','2015-05-15 18:10:34','2015-05-18 16:07:54',1,'手撕鬼子、时空穿越、石头砸飞机……抗日神剧的奇葩桥段已经让我们见怪不怪了。但是，神剧编剧会这样轻易放过你么？','观察者网'),(2,'叫板范爷？张馨予扮花仙子惨遭差评删博','<div align=\"center\">\r\n	<p>\r\n		<span style=\"color:#E53333;\"><img src=\"/pro/upload/2015/5/18/1431936609542.jpg\" alt=\"\" /></span>\r\n	</p>\r\n	<p>\r\n		<img src=\"/pro/upload/2015/5/18/1431936626086.jpg\" alt=\"\" />\r\n	</p>\r\n	<p align=\"left\">\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 据报道,女星张馨予日前穿着一袭红配绿东北棉袄风礼服现身戛纳红毯,衣着品味令人不敢恭维,更是在红毯礼服的比拼上输给了范冰冰,但张馨予似乎不甘示弱,网曝5月16日她又偷偷在微博上传了一张花仙子照来跟冰冰比美,只不过网友好像有些不买账。</span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 据报,5月16日张馨予她便在微博上传自己穿着花仙子装的照片,似乎是想跟范爷一较高下,岂料照片晒出不久就引来网友的差评,反讥她要有自知之明,让张馨予只能黯然删文。甚至有网友起底她过去与李晨交往的情史,毒舌称:“难怪李晨会选范冰冰,不要你。”</span>\r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:16px;\">(原标题：张馨予扮花仙子惨遭差评删博)</span>\r\n	</p>\r\n<br />\r\n<span style=\"color:#E53333;\"></span>\r\n	<p>\r\n		<br />\r\n	</p>\r\n<span style=\"color:#E53333;\"></span>\r\n</div>',0,1,'/upload/2015/5/18/1431921284116.png','2015-05-18 11:54:55','2015-05-18 16:11:28',0,'叫板范爷？张馨予扮花仙子惨遭差评删博','金羊网-新快报'),(3,'中国南海休渔令遭越南抗议 越方称将不予理会','<p align=\"center\">\r\n	<img src=\"/pro/upload/2015/5/18/1431927013543.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	&nbsp;<span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span> <span style=\"font-size:14px;\">从5月16日12时起，南海进入为期两个半月的伏季休渔期。中国这一传统休渔令却在越南引起炸锅般反应。越南《青年报》17日报道称，越南方面已经就\r\n中国对南海(越南称“东海”)施行的本年度休渔令表示抗议，并称不会理会。厦门大学国际关系学院院长庄国土17日对《环球时报》说，休渔本身对保护南海渔\r\n业资源有利，越南政府有义务约束本国渔民，不要因为小纠纷破坏两国目前良好的氛围。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;\">越南最大的新闻门户网站之一“越南每日快讯”报道称，今年的休渔令符合中国惯例，至8月1日结束。这一时间范围内，北纬12度以北至“闽粤海域交界\r\n线”的南海海域(含北部湾)，除单层刺网、钓业外，禁止其他所有作业类型生产。但越南外交部发言人黎海平16日在一份声明中说，休渔令违反了越南在西沙群\r\n岛(越南称“黄沙群岛”)的主权。他声称，“在我们看来这份休渔令是无效的”。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 中国在南海的伏季休渔政策从1999年就开始实施，中国和其他国家的渔船都在限制范围内。据中国官方消息，海南伏季休渔渔船达9000艘。中国方面\r\n称，休渔令是着眼于保护海上渔业资源，提高渔民的生态与环保意识。越南《青年报》援引一名美国分析家的话说，由于南海休渔期间，中国的渔政执法船艇将加大\r\n海上巡逻监管力度，这无疑会彰显中国对南海的主权。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:14px;\">庄国土17日对《环球时报》说，从中国最开始发布南海休渔令，越南每年都会抗议。进入休渔范围海域的渔船都会受到管辖，而中越双方在南海管辖区域上有冲突，所以就带来渔业纠纷。但不只是中国，印尼、菲律宾等国海上执法部门也曾扣留过违反规定的越南渔船。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;\">他说，越南领导人不久前访华，中越都不希望良好的气氛被破坏。中方将注意渔船相关动向，及时予以警告。这种柔性的做法，有利于中越相关争议降温，实现更好地管控争议。</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>',0,1,'/upload/2015/5/18/1431926362046.png','2015-05-18 13:25:39','2015-05-18 13:38:15',0,'中国南海休渔令遭越南抗议 越方称将不予理会','中国广播网'),(4,'小花妹妹会走路啦！邓超发愁女儿难找男友','<p>\r\n	小花妹妹会走路啦\r\n</p>\r\n<p>\r\n	邓超和娘娘的女儿已经一岁啦!5月17日,邓超通过微博晒出一段小花妹妹会走路的视频,视频中,小花妹妹一步一步蹒跚向前,非常的温馨可爱。随后,逗比属性不改的邓超自己抢沙发,担心女儿以爸爸的标准以后很难找到男友,遭到网友的打趣。\r\n</p>\r\n<p>\r\n	5月17日18点27分,邓超通过微博上传女儿小花妹妹走路的视频,感叹道:“小花妹妹会走了,时间好快!”并一连作出大哭的表情。视频中,小花扎着冲天小编,白色斑点上衣,一步一步蹒跚走向前,非常的可爱,画面温馨。\r\n</p>\r\n<p>\r\n	而邓超仍不忘逗比本性,随即自己抢沙发,先是发愁她谈男朋友,“她谈男友怎么办?”接着又担心的称:“以爸爸的标准很担心她找不到男友。”此微博\r\n惹得网友纷纷留言打趣调侃,其中有网友称:“主要是想快点学会走路远离疯癫的爸爸。”也有网友称:“也就意味着你被赶出家门的日子越来越近了。更有网友\r\n称:“小花妹妹都会走了,你怎么还没长大?”(我是弥尔)\r\n</p>',0,1,'','2015-05-18 16:13:05','2015-05-18 16:13:05',0,'小花妹妹会走路啦！邓超发愁女儿难找男友','新浪娱乐'),(5,'刘亦菲晒骑单车美照清新自然 网友唤程铮','<div align=\"center\">\r\n	<p>\r\n		<img src=\"/pro/upload/2015/5/18/1431936880315.jpg\" alt=\"\" />\r\n	</p>\r\n	<p align=\"left\">\r\n		<br />\r\n	</p>\r\n	<p>\r\n		刘亦菲\r\n	</p>\r\n	<p>\r\n		5月17日,刘亦菲晒出一张骑着单车的照片。照片中,刘亦菲戴着墨镜,穿着休闲,气质清新自然。网友大呼:“女神就是近照远照都能驾驭。”“这是在和吴亦凡拍戏中吧?”“程铮学长呢!?快出来。”\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n</div>',0,1,'','2015-05-18 16:15:07','2015-05-18 16:15:07',0,'刘亦菲晒骑单车美照清新自然 网友唤程铮','新浪娱乐'),(6,'冯绍峰倪妮分手 工作室同斥私生活被造谣','<div align=\"center\">\r\n	<p>\r\n		<img src=\"/pro/upload/2015/5/18/1431937043321.jpg\" alt=\"\" />\r\n	</p>\r\n	<p align=\"left\">\r\n		<br />\r\n	</p>\r\n	<p>\r\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 倪妮工作室的声明\r\n	</p>\r\n	<p>\r\n		&nbsp;&nbsp;&nbsp;&nbsp; 5月17日,冯绍峰倪妮被曝三年情断分手,记者联系经纪人求证,对方表示冯绍峰倪妮确实和平分手。而在此后,有营销号以及部分媒体曝出了两人分手\r\n所谓的“理由”。17日晚,冯绍峰和倪妮工作室先后发表严正声明斥责了该谣言,表示两人的分手属和平分手,未有其他因素。希望大家善意祝福两位当事人,不\r\n要再侮辱毁谤他们。(新娱)\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n</div>',0,2,'','2015-05-18 16:19:23','2015-05-18 16:19:23',0,'冯绍峰倪妮分手 工作室同斥私生活被造谣','新浪娱乐'),(7,'朝鲜首次回应“玄永哲被炮决”报道','<div align=\"center\">\r\n	<p>\r\n		<img src=\"/pro/upload/2015/5/18/1431938315731.jpg\" alt=\"\" />\r\n	</p>\r\n	<p align=\"center\">\r\n		玄永哲开会打盹照，左一为玄永哲\r\n	</p>\r\n	<p align=\"left\">\r\n		<br />\r\n	</p>\r\n	<p>\r\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 韩联社17号报道说，朝鲜为纪念今年10月10号劳动党建党七十周年，已组建工作小组筹备阅兵仪式等纪念活动，并可能展示新型洲际导弹，和最近试射成功的潜射导弹等新型武器。\r\n	</p>\r\n	<p>\r\n		据悉，韩国国家情报院13号在国会情报委员会会议上，汇报了朝军二号人物、人民武力部部长玄永哲以叛国罪被处决的情报时，也汇报了朝鲜建党七十周年纪念活动的准备情况。韩方研判,朝方在阅兵式上，可能会公开洲际导弹和最近试射成功的潜射导弹等新型武器，\r\n	</p>\r\n	<p>\r\n		朝中社9号报道了领导人金正恩观看了战略潜艇成功进行弹道导弹水下试射，据报道，这次试射证明朝鲜潜艇发射的弹道导弹各项指标，完全达到最新军事科技要求。不过军事专家认为，朝鲜要超越水中发射，实现最终打击目标物的试射，还需要解决许多技术问题。\r\n	</p>\r\n	<p>\r\n		此外，也有观点认为，朝鲜在建党七十周年前后，将发射远程火箭，并有可能进行第四次核试验。\r\n	</p>\r\n	<p>\r\n		另外，朝鲜17号首次回应有关玄永哲被处决的报道。\r\n	</p>\r\n	<p>\r\n		朝鲜对韩宣传网站“我们民族之间”17日发表声明称，若韩方继续诽谤和冒犯朝鲜最高尊严的话，将用武力应对。\r\n	</p>\r\n	<p>\r\n		声明点名批评韩国总统朴槿惠对朝鲜人民武力部部长玄永哲被处决说朝鲜实施“恐怖统治”，并谴责韩国极右派媒体也提到“肃清政治”，煽动社情舆论。\r\n	</p>\r\n	<p>\r\n		声明称，“傀儡牌党”（指韩国）试图抹黑朝鲜最高尊严和朝鲜体制，这是极其恶劣的挑衅，朝鲜对此予以严厉谴责。朴槿惠牌党再度策划反共和国阴谋，其用意在把民众的目光转向朝鲜。韩方必须立刻停止自灭游戏，向朝鲜道歉。\r\n	</p>\r\n	<p>\r\n		韩国国家情报院有关负责人13日在国会情报委员会进行非公开工作汇报时表示朝军二号人物、人民武力部部长玄永哲近日以叛国罪被处决，金正恩执政后遭到处决的干部逐年增加，共计70余名。\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n</div>',0,2,'','2015-05-18 16:40:02','2015-05-18 16:40:02',0,'朝鲜首次回应“玄永哲被炮决”报道','财经网'),(8,'卫国战争女英雄卓娅：青春卫国 英雄永生','<h1 align=\"center\">\r\n	<img src=\"/pro/upload/2015/5/18/1431938562116.jpg\" alt=\"\" />\r\n</h1>\r\n<p align=\"center\">\r\n	这是位于俄罗斯首都莫斯科西南彼得里谢沃村的卓娅纪念馆。\r\n</p>\r\n<p align=\"left\">\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1923年,卫国战争女英雄卓娅·科斯莫杰米扬斯卡娅出生在坦波夫州。经历幼年丧父后,卓娅和弟弟舒拉随母亲辗转来到莫斯科。1941年6月,德国\r\n对俄罗斯发动闪电战,秋冬时节逼近莫斯科。当人们撤离莫斯科时,卓娅选择留下并加入由优秀共青团员组成的苏军西部方面军游击队。11月下旬,卓娅和战友奉\r\n命前往被德军占领的彼得里谢沃烧掉德军房屋。完成部分任务后,她决定返回村中烧掉存放有德军器械和马匹的马厩。但是,她将汽油浇下还没来得及点火时被逮\r\n捕。挨过数小时拷打折磨后,卓娅于次日被德军绞死,牺牲时年仅18岁。\r\n</p>\r\n<p>\r\n	1942年2月,苏联最高苏维埃追认卓娅为英雄。她成为苏联卫国战争中第一位被授予英雄称号的女兵。苏联各地树立卓娅的雕像,用各种方式宣传她的英雄事迹。在中国,《卓娅和舒拉的故事》也影响整整一代人的成长。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>',0,2,'','2015-05-18 16:43:54','2015-05-18 16:43:54',0,'卫国战争女英雄卓娅：青春卫国 英雄永生','军事'),(9,'关于我们','<div class=\"article-box\">\r\n	<p align=\"center\">\r\n		<strong>易物恒通（北京）国际投资股份有限公司（以下简称“易物天下”）是目前中国领先的B2B易物平台。</strong>\r\n	</p>\r\n	<p>\r\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 易物天下由侯晶女士创立于2010年。从创立伊始，易物天下就坚持以用户体验为最高诉求，致力于创造便捷、高效、值得信赖的企业级以物易物网络\r\n平台。以物易物的模式源于远古时期，从山顶洞人时期的食物换衣物，到西汉时期的美誉中华海外的丝绸之路，以物易物自产生至今已经影响了人类几千年。如今，\r\n易物天下创新推出基于先进的互联网技术和IT技术的现代化易物网络平台。借助易物天下的平台，企业可以真正实现在线物品发布、管理、在线洽谈、下单交换等\r\n功能，从而实现易物清库，易物营销，易物变现、现金流节省和资产保值增值等目的。\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p class=\"b_title\">\r\n		为了帮助众多中小企业解决困扰企业发展的现金流周转困难、开发客户渠道受限、库存产品积压等问题，易物天下创新提出了打破传统经营方式的四大易物模式：\r\n	</p>\r\n	<div>\r\n		1)<b>“易物清库”</b>即清理库存，促进现金流快速周转；\r\n	</div>\r\n	<div>\r\n		2)<b>“易物采购”</b>即用自己的产品换成需要的产品，节省资金；\r\n	</div>\r\n	<div>\r\n		3)<b>“易物营销”</b>即当产品换出去的同时，商品接触了更多潜在客户，从而快速拓展市场；\r\n	</div>\r\n	<div>\r\n		4)<b>“易物投资”</b>即通过以物易物，达到资产增值。客户可以低成本甚至零成本快速的进行市场扩张，彻底解决企业经营中市场拓展渠道难，成本高等不利因素，为中小企业搭建一个低成本经营的资源平台，让更多的企业体验易物带来的非凡魅力，达到资产增值的目的。\r\n	</div>\r\n	<p>\r\n		易物天下作为区别于现金交易的平台优势显而易见，企业可以在平台上易出其库存滞销的产品，同时又能易入原本需要用现金采购的原材料等产品，从而解决了现金交易中难以解决的问题。作为现金交易方式的补充，它将起到越来越重要的作用。\r\n	</p>\r\n	<p>\r\n		易物天下独创的四大易物模式，线下在全国各地成功举办《易物天下总裁见面会》数千场，组成遍布全国的多元化营销网络体系；同时依托品牌魅力和互联网技术，开展网络平台服务，并积极完善易物服务体系，极大的促进了区域易物交易的增长，拓展了跨行业的经济往来。\r\n	</p>\r\n	<p>\r\n		易物天下通过“以物易物”的模式为客户实现资源整合，为中小企业提供完善的平台技术开发，技术提供服务。帮助众多中小企业，解决困扰企业发展的现金流周转困难、开发客户渠道受限、库存产品积压等问题。\r\n	</p>\r\n	<p>\r\n		2013年11月15日，易物天下正式启动试运营中国首个专业性易物网络平台——“易物天下网”（<a href=\"http://www.i1515.com\">www.i1515.com</a>），\r\n并取得7个《计算机软件著作权》，该网站集国内权威、专业、规范等特色于一体，将古老的以物易物模式嵌入了互联网技术，开始以一种崭新的姿态面世。易物天\r\n下积累了丰富的线下易物经验开发了大量的客户资源，现其对线上B2B平台的转型已全面启动。这也是易物天下网络平台的长远发展战略中重要的一环。易物天下\r\n正在申请国家高新技术认定。\r\n	</p>\r\n	<p>\r\n		易物天下网络平台拥有强大、专业的易物服务团队，完善的在线物品发布管理系统，采用易物网络平台模式、物联网技术来服务企业在线易物、采购、销售，有利于促进企业间通过该平台实现全国化的渠道增值、业务拓展以及销售增长。\r\n	</p>\r\n	<p>\r\n		易物天下网与银联合作，由银联开发网站支付端，银联但保资金安全，从而极大地保障了线上交易的安全，易物天下的服务逐渐被更多客户认可。\r\n	</p>\r\n	<p>\r\n		2013年易物天下进入井喷式发展阶段，截止到2013年11月30日，在全国各省市签约易物公司数量近200家。\r\n	</p>\r\n	<p>\r\n		2014年7月3日，公司完成股份改制，成立易物恒通（北京）国际投资股份有限公司。\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		目前，易物天下提出“科学发展，有效整合资源”的发展战略，努力把易物天下打造成国内领先的易物网络平台。随着智能手机、平板电脑等移\r\n动设备的普及，移动终端交易正呈现爆发式增长的势头，易物天下瞄准移动互联网发展的新形势，着力实现移动互联网对接、打通线上线下手机端，实现信息实时沟\r\n通，让客户可以多渠道管理运营，成为易物行业真正的赢家。\r\n	</p>\r\n	<p>\r\n		易物天下B2B+OTO商业模式，(OTO是“Online To \r\nOffline”的简写，即“线上到线下”)。易物天下融合了B2B商业模式，完善B2B+OTO模式，借力线下完善线上。利用先进的互联网平台技术、真\r\n正实现在线物品发布管理、在线洽谈下单交换、实现变现及易物于一体化经营的网络平台。\r\n	</p>\r\n	<p>\r\n		历经数年的发展，易物天下网累计注册用户数近百万。无论从其交易数据还是从其在国内同行业的影响力上来看，易物天下无疑已经走在中国易物贸易行业的前列，是一个名副其实的易物服务航母集群。\r\n	</p>\r\n	<p>\r\n		易物天下“让天下没有难卖的产品”经营理念已深入人心；中国商务部发布了《关于促进电子商务应用的实施意见》。易物天下将借助国家产业政策的大\r\n好形势，并借助上海自贸区政策和影响力，引领本土企业走出国门、走向世界！未来两年易物天下将迅猛发展，成为面向全球用户、拥有数百万家商铺、上亿种商品\r\n交易信息，年交易额数百亿的大型易物交易平台。\r\n	</p>\r\n	<p>\r\n		为了促进易物行业的发展，易物天下在全国的公司，纷纷设立了地区性易物行业协会和易物商会。易物天下正在筹办设立北京易物商会和全国性易物行业协会。\r\n	</p>\r\n<br />\r\n	<div class=\"ser_list\">\r\n		<h3 class=\"ser_tit\">\r\n			关于联盟合作事项的说明\r\n		</h3>\r\n		<p>\r\n			中国易物天下国际投资有限公司（以下简称“易物天下”）目前是中国易物行业唯一一家获得商务部特许经营权备案的企业，易物恒通（北京）国际投资有限公司是易物天下授权的运营商，拥有易物天下网（www.i1515.com），在全国范围内开展易物联盟招商经营活动。\r\n		</p>\r\n		<p>\r\n			易物天下的董事长侯晶女士创立易物天下后，公司持续经营，在拥有两个直营店的基础上，2013年初易物天下进入井喷式发展阶段，在全国各省市设立的专业易物公司（联盟合作商）数量分布在全国多个地级以上城市。\r\n		</p>\r\n		<p>\r\n			易物天下拥有易物天下品牌使用权，该品牌为关联公司世朋名人（北京）文化交流有限公司申请注册。世朋名人公司法定代表人侯敏，注册资本200万元，住所地北京市朝阳区东三环中路39号建外soho24号楼。该公司至今持续经营。易物天下的经营资源不涉及诉讼或仲裁。\r\n		</p>\r\n		<p>\r\n			易物天下网（www.i1515.com）是国内领先的在线物品交换、变现平台。是中国第一个采取银联担保措施的易物网络平台。\r\n		</p>\r\n		<p>\r\n			加入易物天下的联盟合作费项目有服务费、品牌及相关权利使用费、保证金。服务费：每年向交纳服务费。品牌及相关权利使用费（具体金额根据不同区域的经济发展水平、人口等因素有所不同）。\r\n		</p>\r\n		<p>\r\n			易物天下不需要联盟商购买任何产品、服务或设备。\r\n		</p>\r\n		<p>\r\n			易物天下对联盟商进行开业培训、其他不定期培训。培训的时间、地点、期限具体于每次培训之前发通知。易物天下每个月将向加盟商提供讲师培训、为期三天。\r\n		</p>\r\n		<p>\r\n			易物天下为联盟商提供持续服务。\r\n		</p>\r\n		<ul>\r\n			<li>\r\n				1.易物天下对加盟商的合作经营情况进行检查、督导和考核。\r\n			</li>\r\n			<li>\r\n				2. 提供有关经营模式、统一标准等有关的书面资料，对线下易物活动进行市场保护等。\r\n			</li>\r\n			<li>\r\n				3. 指导加盟商及其他终端客户所提供的产品统一展示。\r\n			</li>\r\n			<li>\r\n				4. 引导加盟商参加网上易物交易。\r\n			</li>\r\n			<li>\r\n				5. 建立与加盟商在全国范围内开拓市场的利益分割机制。\r\n			</li>\r\n			<li>\r\n				6. 制定限制加盟商在合同关系存续期间同业竞争机制。\r\n			</li>\r\n			<li>\r\n				7.维护甲方及易物天下平台形象、声誉和利益，对加盟商检查、督导、考核等。\r\n			</li>\r\n			<li>\r\n				8. 制定加盟商对外发布信息的要求和惩罚机制。\r\n			</li>\r\n			<li>\r\n				9. 建立加盟商交易产品合规性规则、产品信息发布规则。\r\n			</li>\r\n			<li>\r\n				10. 控制加盟商收费标准。\r\n			</li>\r\n			<li>\r\n				11. 建立加盟商退出机制。\r\n			</li>\r\n			<li>\r\n				12. 对加盟品牌进行严格的知识产权保护。\r\n			</li>\r\n			<li>\r\n				13. 建立易物商业模式、易物数据和客户信息保密制度并采取保密措施。\r\n			</li>\r\n			<li>\r\n				14. 制定并发布易物相关标准、规则。\r\n			</li>\r\n		</ul>\r\n		<p>\r\n			联盟商接受考核，按照合同约定承担责任。\r\n		</p>\r\n		<p>\r\n			易物天下作为经营平台，不对消费者投诉和赔偿承担连带责任。\r\n		</p>\r\n		<p>\r\n			易物天下城市联盟商的投资预算：\r\n		</p>\r\n		<p>\r\n			包括联盟合作费；培训费（暂无单项收费）；举例：房屋租金216000元（按 照200平方米，每平方米3元/日），装修费用6万元；设备、办公用品、家具等购置费6万元；水、电、气费2万元；为取得执照和其他政府批准所需的费用：无；启动周转资金15万元。\r\n		</p>\r\n		<p>\r\n			联盟商按照城市区域加盟，在约定区域内享有权利。\r\n		</p>\r\n		<p>\r\n			2012年易物天下运营平台经审计的净利润为490,728.50元，反映了平台成立之初投资搭建系统，重在投入；2013年1月开始通过联盟合作开始大幅度盈利。\r\n		</p>\r\n		<p>\r\n			易物天下最近5年内无与特许经营相关的重大诉讼和仲裁情况。\r\n		</p>\r\n		<p>\r\n			易物天下及其法定代表人无重大违法经营记录情况，重大违法经营记录。\r\n		</p>\r\n		<div class=\"text_cp\" align=\"right\">\r\n			中国易物天下国际投资有限公司<br />\r\n易物恒通（北京）国际投资有限公司<br />\r\n2013年1月\r\n		</div>\r\n	</div>\r\n	<div align=\"right\">\r\n		易物恒通（北京）国际投资股份有限公司\r\n	</div>\r\n	<div align=\"right\">\r\n		电话：400-688-3913\r\n	</div>\r\n	<div align=\"right\">\r\n		网址：<a href=\"http://www.i1515.com\">www.i1515.com</a>\r\n	</div>\r\n	<div align=\"right\">\r\n		公司地址：北京朝阳区酒仙桥路甲12号电子城科技大厦6层606-609室\r\n	</div>\r\n</div>',0,3,'','2015-05-20 10:29:10','2015-05-20 10:29:10',0,'关于我们','超级管理员'),(10,'诚聘英才','<div class=\"article-box\">\r\n	<div class=\"article_con\">\r\n		<p>\r\n			<strong><span style=\"color:#ff0000;\"><span style=\"font-size:large;\">客服部 电销专员6名</span></span></strong><br />\r\n<strong><span style=\"font-size:small;\">工作职责：</span></strong><br />\r\n<span style=\"font-size:x-small;\"><span style=\"font-size:small;\">1、通过电话与客服沟通，对网站待处理订单进行有效跟进；<br />\r\n2、不断挖掘潜在客观，完成公司制定的业绩要求；<br />\r\n3、协调与客户关系，及时向公司反馈相关信息。</span></span><br />\r\n<strong><span style=\"font-size:small;\">任职要求：</span></strong><br />\r\n<span style=\"font-size:small;\">1、有意愿从事电子商务销售工作优先；<br />\r\n2、具有较强的人际沟通能力，表达能力，适应能力和学习能力较强；<br />\r\n3、熟悉电子商务，尤其是有B2B网站经验优先；<br />\r\n4、有电话销售工作经验者优先。</span><br />\r\n<span style=\"font-size:small;\"><strong>工作时间及福利：</strong></span><span style=\"font-size:small;\">早九晚六，双休，节假日休息，节日礼品，浮动奖金等。</span>\r\n		</p>\r\n		<p>\r\n			&nbsp;\r\n		</p>\r\n		<p>\r\n			<strong><span style=\"color:#ff0000;\"><span style=\"font-size:large;\">客服部 网络客服6名</span></span></strong><br />\r\n<span style=\"font-size:small;\"><strong>工作职责</strong></span><br />\r\n<span style=\"font-size:small;\">1、为客户提供完整准确的方案及信息，解决客户问题，提供高质量服务； <br />\r\n2、回复电话咨询和网络咨询； <br />\r\n3、接受和网络订单，处理订单； <br />\r\n4、回访和维护客户，服务订单。 </span><br />\r\n<span style=\"font-size:small;\"><strong>任职资格</strong></span><br />\r\n<span style=\"font-size:small;\">1、有客服和电话销售经验优先，勤奋敬业、责任心强，思路清晰；<br />\r\n2、普通话标准，口齿伶俐，语言表达能力强，擅于沟通；<br />\r\n3、有耐心及责任心，身体健康，能够长期稳定的干好工作；电脑使用熟练，熟悉办公软件和网络工具。</span><br />\r\n<span style=\"font-size:small;\"><strong>工作时间及福利：</strong></span><span style=\"font-size:small;\">早九晚六，双休，节假日休息，节日礼品，浮动奖金等。</span>\r\n		</p>\r\n		<p>\r\n			<br />\r\n<span style=\"font-size:large;\"><br />\r\n<strong><span style=\"color:#ff0000;\">会务部 会议营销讲师20名</span></strong></span><br />\r\n<span style=\"font-size:small;\"><strong>岗位职责： </strong></span><br />\r\n<span style=\"font-size:small;\">1、按公司统一规定做好销讲内容准备；<br />\r\n2、面向不同地区、行业、身份的客户进行销讲内容调整，确保内容、市场、风格符合需求；<br />\r\n3、在推广工作中做好现场把控，敏锐判断客户投入状态，灵活调整销讲风格与内容；<br />\r\n4、向关键客户或合作商阐述项目内容与价值；<br />\r\n5、与业务和产品体系做好相互呼应、彼此支持、促动项目的现场成交；<br />\r\n6、可接受短期出差。</span><br />\r\n<span style=\"font-size:small;\"><strong>任职资格：</strong></span><br />\r\n<span style=\"font-size:small;\">1、市场营销相关专业，五年以上招商、营销、销售工作经验；<br />\r\n2、具备良好的气质、形象；<br />\r\n3、具备极强的沟通能力、良好的演讲能力；<br />\r\n4、富有激情，具有感染力；<br />\r\n5、思维敏捷，妥善处理现场出现的异常问题。</span><br />\r\n<span style=\"font-size:small;\"><strong>薪资待遇</strong>：</span><span style=\"font-size:small;\">基本工资+提成，根据个人能力月薪可达2W-20W。</span>\r\n		</p>\r\n		<p>\r\n			&nbsp;\r\n		</p>\r\n		<p>\r\n			<strong><span style=\"color:#ff0000;\"><span style=\"font-size:large;\">技术经理1名</span></span></strong><br />\r\n<span style=\"font-size:small;\"><strong>岗位职责</strong>：</span><br />\r\n<span style=\"font-size:small;\">1、负责公司网站的开发和制作，及后期运营维护；<br />\r\n2、负责公司服务器及计算机网络系统的管理和维护，并保障安全正常运行；<br />\r\n3、公司其他网络或软件产品的研发和设计；<br />\r\n4、完成上级交付的其它技术开发和网络管理任务。</span><br />\r\n<span style=\"font-size:small;\"><strong>任职要求</strong>：</span><br />\r\n<span style=\"font-size:small;\">1、本科及以上学历，计算机软件系统相关专业毕业，5年以上PHP编程经验，java开发2年经验；<br />\r\n2、有较强的团队协作意识和积极负责的工作态度，能独立完成任务，良好的表达与沟通能力，持续的学习能力；<br />\r\n3、具有独立开发能力，有大型企业网站或门户网站项目开发经验者优先；有商城系统开发经验者优先。</span>\r\n		</p>\r\n		<p>\r\n			&nbsp;\r\n		</p>\r\n		<p>\r\n			<strong><span style=\"color:#ff0000;\"><span style=\"font-size:large;\">技术部 Android开发工程师2名</span></span></strong