/*Table structure for table `t_product` */

CREATE TABLE `t_product` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `title` varchar(32) DEFAULT NULL COMMENT '产品标题',
  `remark` varchar(255) DEFAULT NULL COMMENT '产品概述',
  `type` int(11) DEFAULT NULL COMMENT '产品类型 0:葡萄',
  `content` text COMMENT '产品内容描述',
  `state` int(11) DEFAULT '0' COMMENT '状态 0:正常 1:无效',
  `sort` int(11) DEFAULT '0' COMMENT '排序',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `update_time` datetime DEFAULT NULL COMMENT '更新时间',
  `image` varchar(255) DEFAULT NULL COMMENT '产品展示图片',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

/*Data for the table `t_product` */

insert  into `t_product`(`id`,`title`,`remark`,`type`,`content`,`state`,`sort`,`create_time`,`update_time`,`image`) values (1,'红提葡萄','红提葡萄又名红地球，欧亚种。原产于美国加州，是由美国加利福尼亚州立大学研究人员于70年代进行杂交试验，培育而成的一个葡萄品种。自我国1987年引入该品种以来，华北及西北大部分地区栽培表现极好，果实品质优，晚熟，耐贮运，丰产，是发展葡萄的首选优质高效品种。目前来说，中国的产区主要分布在：陕西渭南，新疆，云南，甘肃，宁夏，黄河故道，昌黎，胶东半岛，江苏宝应，沈阳 。其中，三贤故里的渭南临渭区种植地，因红提葡萄品质好，面积大，最为有名。',1,'<p style=\"text-align:center;\">\r\n	&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img src=\"/pro/upload/2015/5/20/1432101055958.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span style=\"background-color:#E53333;\">&nbsp;红提葡萄</span>又名红地球，欧亚种。原产于美国加州，是由美国加利福尼亚州立大学研究人员于70年代进行杂交试验，培育而成的一个葡萄品种。自我国1987年引入该品种以来，华北及西北大部分地区栽培表现极好，果实品质优，晚熟，耐贮运，丰产，是发展葡萄的首选优质高效品种。目前来说，中国的产区主要分布在：陕西渭南，新疆，云南，甘肃，宁夏，黄河故道，昌黎，胶东半岛，江苏宝应，沈阳 。其中，三贤故里的渭南临渭区种植地，因红提葡萄品质好，面积大，最为有名。\r\n</p>',0,0,'2015-05-18 15:07:43','2015-05-20 13:58:38','/upload/2015/5/18/1431936254360.jpg'),(2,'巨峰葡萄','巨峰葡萄，属中熟类、四倍体品种，欧美杂交种，原产日本。大井上康于1937年用石原早生为母本，森田尼为父本杂交培育。1959年引入中国，并在全国各地大面积推广，成为深受果农欢迎的主栽品种。',1,'<p style=\"text-align:center;\">\r\n	&nbsp; &nbsp; &nbsp; <img src=\"/pro/upload/2015/5/20/1432101738411.jpg\" alt=\"\" />&nbsp;&nbsp;\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#E53333;\">&nbsp;巨峰葡萄是生</span>产中的主栽品种之一，适应性强，抗病、抗寒性能好，喜<a target=\"_blank\" href=\"http://baike.baidu.com/view/409269.htm\">肥水</a>。果实<a target=\"_blank\" href=\"http://baike.baidu.com/view/687508.htm\">穗</a>大，粒大，平均穗重400-600克，平均果粒重12克左右，最大可达20<a target=\"_blank\" href=\"http://baike.baidu.com/view/74027.htm\">克</a>。8月下旬成熟，成熟时<a target=\"_blank\" href=\"http://baike.baidu.com/view/9979667.htm\">紫黑色</a>，果皮厚，<a target=\"_blank\" href=\"http://baike.baidu.com/view/2060541.htm\">果粉</a>多，果肉较软，味甜、多汁，有<a target=\"_blank\" href=\"http://baike.baidu.com/view/3663.htm\">草莓</a>香味，皮、肉和种子易分离，含<a target=\"_blank\" href=\"http://baike.baidu.com/view/49321.htm\">糖</a>量16%。\r\n</p>\r\n巨峰葡萄败花严重，坐果率低，果穗松散，重摘心可以提高巨峰葡萄坐果率，增加<a target=\"_blank\" href=\"http://baike.baidu.com/view/1295893.htm\">产量</a>，改善<a target=\"_blank\" href=\"http://baike.baidu.com/view/223584.htm\">品质</a>。葡萄开花前7-10天，结果枝在<a target=\"_blank\" href=\"http://baike.baidu.com/view/146023.htm\">花穗</a>以上保留2-3张叶片、<a target=\"_blank\" href=\"http://baike.baidu.com/view/6262490.htm\">营养枝</a>保留5-6张叶片摘心，主蔓延长枝到立秋前后摘心。结果枝和营养枝除保留先端1个副梢外，其余副梢全部抹除。结果枝和营养枝先端的副梢长到一定长度时分别保留6-8张叶片和8-10张叶片<a target=\"_blank\" href=\"http://baike.baidu.com/view/186928.htm\">摘心</a>，其上发生的两次副梢和主蔓<a target=\"_blank\" href=\"http://baike.baidu.com/view/2479661.htm\">延长枝</a>的副梢均保留1张叶片反复摘心。在摘心和处理副梢的同时，根据负载量疏去多余果枝、营养枝和<a target=\"_blank\" href=\"http://baike.baidu.com/view/700518.htm\">果穗</a>，留下的果穗去掉穗尖的1/4至1/5，并去掉副穗',0,0,'2015-05-18 16:00:06','2015-05-20 14:06:19','/upload/2015/5/18/1431935978612.jpg');

/*Table structure for table `t_product_type` */

CREATE TABLE `t_product_type` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `type_name` varchar(32) DEFAULT NULL COMMENT '产品类型名称',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `update_time` datetime DEFAULT NULL COMMENT '更新时间',
  `state` int(11) DEFAULT '0' COMMENT '0:可用 1:不可用',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;

